import React from 'react'

 const ModalPass = ({show}) => {
    if (!show) {
        return null;
      }
    return (
        <div className="modal">
        <div className="modal-content">
          <div className="modal-header">
            <div className="modal-body">
              <h3 className="parageraph p-title" > رمز عبور به ایمیل شما ارسال شد </h3>
            </div>
            <button className="button" onClick={on}>close</button>
          </div>
        </div>
      </div>
    )
}
export default ModalPass;
