               <input className="top-input" type="text" name ="name" value={firstName} onChange={(e) => {setFirstName(e.target.value)}} placeholder="نام"/>
   
               {<div style={{color:"red"}}> {firstNameErr.errFirstName}</div>}
               </div>
               <div className="inner-input">
               <input className="top-input" type="text" name ="last-name" value={lastName} onChange={(e) => {setLastName(e.target.value)}} placeholder=" نام خانوادگی"/>
               {<div style={{color:"red"}}> {firstNameErr.errFirstName}</div>}
               </div>
           </div>
            <input type="email" name="email" className="register-input" placeholder="پست الکترونیک"/>
            <input type="text" name="education" className="register-input" placeholder="تحصیلات" onChange={(e) => setChange(e.target.value)}/>
            {/* {showFeildLocation} */}
            {/* <input type="text"  className="register-input" placeholder="محل تحصیل"/> */}
{
   change !== "" ? (<input type="text"  className="register-input" placeholder="محل تحصیل"/>) : (<div></div>)
}
            <Dropdown />
