import React, { useState } from "react";
import ModalPass from "./ModalPass";
const ForgetForm = ({ show }) => {
  if (!show) {
    return null;
  }
  const [email, setEmail] = useState("");
  const [showMsg, setShowMsg] = useState("");
  
  const msgPass = (e) => {
    e.preventDefault();
    // const regex = RegExp(
    //   /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
    // );
    if (email) {
      return false;
    }
    setShowMsg(true);
  };
  return (
    <div className="modal">
      <div className="modal-header-form">
        <div className="modal-body-form">
          <form onSubmit={msgPass}>
            <div className="login-box box-container forget-form">
              <h3>فرم فراموشی رمز </h3>

              <input
                type="email"
                name="email"
                className="login-input"
                value={email}
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
                placeholder="پست الکترونیک"
              />
              <button type="submit" className="login-btn btn">
                ثبت
              </button>
              <ModalPass
                onClose={() => setShowMsg(false)}
                showMsg={setShowMsg}
              />
            </div>
          </form>
        </div>
      </div>
    </div>

    // <div className="modal-form">

    // <div className="modal-body">
    //      <form >
    //  <div className="login-box box-container">
    //      <h3>فرم فراموشی رمز </h3>
    //     {/* <div className="input-group">  */}

    //     <input type="email" name="email" className="login-input" placeholder="پست الکترونیک"/>
    //     <button type="submit" className="login-btn btn">ورود</button>
    //  </div>
    //  </form>

    // </div>

    // </div>

    // <form >
    // <div className="login-box box-container">
    //     <h3>فرم فراموشی رمز </h3>
    //     {/* <div className="input-group">  */}

    //     <input type="email" name="email" className="login-input" placeholder="پست الکترونیک"/>
    //   </div>
    // </form>
  );
};
export default ForgetForm;
