import React from 'react';
class Dropdown extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			province : [],
			// states : [],
			cities : [],
			selectedCountry : 'انتخاب استان',
			// selectedState : '--Choose State--'
		};
		this.changeCountry = this.changeCountry.bind(this);
		// this.changeState = this.changeState.bind(this);
	}
  
	componentDidMount() {
		this.setState({
			province : [
				{ name: 'تهران',  cities: ['تهران', 'شهریار'] },
				{ name: 'خراسان رضوی', cities: ['نیشابور', 'مشهد'] },
				{ name: 'شیراز', cities: ['مرودشت', 'شیراز'] },
				{ name: 'اصفهان', cities: ['کاشان', 'اصفهان'] },
				{ name: 'تبریز', cities: ['میانه', 'تبریز'] },
				{ name: 'مازندران', cities: ['بابل', 'ساری'] },
				{ name: 'گیلان', cities: ['لاهیجان', 'رشت'] },
				{ name: 'بوشهر', cities: ['گناوه', 'بوشهر'] },
				{ name: 'کرمانشاه', cities: ['تهران', 'کرمانشاه'] },
				{ name: 'خوزستان', cities: ['تهران', 'شهریار'] },
			]
		});
	}
  
	changeCountry(event) {
		this.setState({selectedCountry: event.target.value});
		this.setState({states : this.state.province.find(cntry => cntry.name === event.target.value).states});
	}

	changeState(event) {
		this.setState({selectedState: event.target.value});
		const stats = this.state.province.find(cntry => cntry.name === this.state.selectedCountry).states;
		this.setState({cities : stats.find(stat => stat.name === event.target.value).cities});
	}
	
	render() {
		return (
			<div id="container">
				<h2>Cascading or Dependent Dropdown using React</h2>
	
				<div>
					<label>Country</label>
					<select placeholder="Country" value={this.state.selectedCountry} onChange={this.changeCountry}>
						<option>--Choose Country--</option>
						{this.state.province.map((e, key) => {
							return <option key={key}>{e.name}</option>;
						})}
					</select>
				</div>

				<div>
					<label>State</label>
					<select placeholder="State" value={this.state.selectedState} onChange={this.changeState}>
						<option>--Choose State--</option>
						{this.state.states.map((e, key) => {
							return <option key={key}>{e.name}</option>;
						})}
					</select>
				</div>
				
				<div>
					<label>City</label>
					<select placeholder="City">
						<option>--Choose City--</option>
						{this.state.cities.map((e, key) => {
							return <option key={key}>{e}</option>;
						})}
					</select>
				</div>
			</div>
		)
	}
}

export default Dropdown;
