    return (
        <div className="register-box box-container">
            <h3>رایگان ثبت نام کنید</h3>
            {/* <div className="input-group">  */}
           <div className="group-name">
               <input type="text" name ="name" placeholder="نام"/>
               <input type="text" name ="last-name" placeholder=" نام خانوادگی"/>
           </div>
            <input type="email" name="email" className="register-input" placeholder="پست الکترونیک"/>
            <input  type={passShown ? "text" : "password"} name="password" className="register-input " placeholder="کلمه عبور"/>
            <FaEye onClick={togglePassVisiblity} className={`open-eye icon ${!passwordShown && "notVisible"}`}   />
            <FaEyeSlash onClick={togglePassVisiblity} className={`close-eye icon ${passwordShown && "notVisible"}`} />
           <button type="button" className="register-btn btn" onClick={submitRegister}>ثبت نام</button>
            {/* </div> */}
        </div>
    )
}
export default RegisterBox;
