import React, { useState } from "react";
import { FaEye } from "react-icons/fa";
import { FaEyeSlash } from "react-icons/fa";
import Dropdown from "./Dropdown";
// import { useForm } from "react-hook-form";
const RegisterBox = ({ passwordShown, toggleClick, ...props }) => {
    /* useform
      // const { register, handleSubmit } = useForm();
      // const onSubmit = data => {
      //   console.log(data);
      // };
      // ref={register({ required: "This is required." })}
  */
    // const [passShown, setPassShown] = useState(false);
    // const togglePassVisiblity = () => {
    //     setPassShown(passShown ? false : true);
    //   };
    const [change, setChange] = useState("");
    // const [errors, setErrors] = useState([""])
    //     const showFeildLocation = (e) => {
    //         console.log(e.target.value);
    //   if (e.target.value !== "") {
    //     return <input type="text"  className="register-input" placeholder="محل تحصیل"/>;
    //   }
    // };
    // console.log(passwordShown);
    // const showValidationErr = (elm,msg) {

    // }
    // const  clearValidationErr = () {

    // }
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [firstNameErr, setFirstNameErr] = useState({});
    const [lastNameErr, setLastNameErr] = useState({});

    const onSubmit = (e) => {
        e.preventDefault();
        console.log(1111);
        const isValid = formValdation();
    };
    const formValdation = () => {
        console.log(222);
        // const firstNameErr ={}
        // const lastNameErr ={}
        let isValid = true;
        if (firstName === "") {
            firstNameErr = "لطفا نام خود را وارد کنید";
            isValid = false;
        }
        if (lastName === "") {
            lastNameErr = "لطفا نام خانوادگی خود را وارد کنید";
            isValid = false;
        }
        setFirstNameErr(firstNameErr);
        setLastNameErr(lastNameErr);
        return isValid;
    };

    const submitRegister = (e) => { };
    return (
        <form onSubmit={onSubmit}>
            <div className="register-box box-container">
                <h3>رایگان ثبت نام کنید</h3>
                {/* <div className="input-group">  */}
                <div className="group-name">
                    <div className="inner-input">
                        <input
                            className="top-input"
                            type="text"
                            name="name"
                            value={firstName}
                            onChange={(e) => {
                                setFirstName(e.target.value);
                            }}
                            placeholder="نام"
                        />

                        {<div style={{ color: "red" }}> {firstNameErr}</div>}
                    </div>
                    <div className="inner-input">
                        <input
                            className="top-input"
                            type="text"
                            name="last-name"
                            value={lastName}
                            onChange={(e) => {
                                setLastName(e.target.value);
                            }}
                            placeholder=" نام خانوادگی"
                        />
                        {<div style={{ color: "red" }}> {lastNameErr}</div>}
                    </div>
                </div>
                <input
                    type="email"
                    name="email"
                    className="register-input"
                    placeholder="پست الکترونیک"
                />
                <input
                    type="text"
                    name="education"
                    className="register-input"
                    placeholder="تحصیلات"
                    onChange={(e) => setChange(e.target.value)}
                />
                {/* {showFeildLocation} */}
                {/* <input type="text"  className="register-input" placeholder="محل تحصیل"/> */}
                {change !== "" ? (
                    <input
                        type="text"
                        className="register-input"
                        placeholder="محل تحصیل"
                    />
                ) : (
                    <div></div>
                )}
                <Dropdown />
                <div className="pass-wrapper">
                    <input
                        type={passwordShown ? "text" : "password"}
                        name="password"
                        className="register-input"
                        placeholder="کلمه عبور"
                    />
                    <FaEye
                        onClick={toggleClick}
                        className={`open-eye icon-register ${!passwordShown && "notVisible"
                            }`}
                    />
                    <FaEyeSlash
                        onClick={toggleClick}
                        className={`close-eye icon-register ${passwordShown && "notVisible"
                            }`}
                    />
                </div>
                <button
                    type="submit"
                    className="register-btn btn"
                    onClick={submitRegister}
                >
                    ثبت نام
        </button>
                {/* </div> */}
            </div>
        </form>
    );
};
export default RegisterBox;
